  def finalFloor(input: String) =
    input.foldLeft(0)((acc, char) => char match {
      case '(' => acc + 1
      case ')' => acc - 1
    })

  val partOne = finalFloor(input)


  val partTwo = (0 to input.length).find(l => finalFloor(input.substring(0, l)) < 0)
